{% block body %}
    <section id=\"content-wrapper\" class=\"form-elements\">

        <div class=\"site-content-title\">
            <ol class=\"breadcrumb float-xs-right\">
                <li class=\"breadcrumb-item\">
                    <span class=\"fs1\" aria-hidden=\"true\" data-icon=\"\"></span>
                    <a href=\"#\">Home</a>
                </li>
                <li class=\"breadcrumb-item\"><a href=\"#\">Classe</a></li>
                <li class=\"breadcrumb-item active\">index</li>
            </ol>

        </div>

        <div class=\"contain-inner dashboard-v2\">
            <div class=\"col-md-12\">
                <div class=\"row\">
                    <div class=\"col-md-12 col-lg-12 col-xl-12 xl-dashboard-v2\">
                        <div class=\"row\">
                            <div class=\"col-md-12 lg-dashboard-v2\">
                                <div class=\"content\">
                                    <div class=\"dashboard-content\">
                                        <div class=\"dashboard-header\">
                                            <h4 class=\"page-content-title float-xs-left\">liste des unites d'enseignement </h4>
                                            <div class=\"dashboard-action\">
                                                <ul class=\"right-action float-xs-right\">
                                                    <li data-widget=\"collapse\">
                                                        <a href=\"javascript:void(0)\" aria-hidden=\"true\"><span
                                                                    class=\"icon_minus-06\" aria-hidden=\"true\"></span></a>
                                                    </li>
                                                    <li data-widget=\"close\"><a href=\"javascript:void(0)\"><span
                                                                    class=\"icon_close\" aria-hidden=\"true\"></span></a>
                                                    </li>
                                                </ul>
                                            </div>
                                        </div>
                                        <div class=\"row\">
                                            <div class=\"col-xs-12\">
                                                <button class=\"btn btn-primary site-btn pull-right\"
                                                        data-target=\"#view-add\" data-toggle=\"modal\">
                                                    Ajouter
                                                </button>
                                            </div>
                                        </div>
                                        <div class=\"dashboard-box\">
                                            <div class=\"basic_table table-responsive\">
                                                <table data-plugin=\"datatable\"  class=\"table table-striped table-hover display nowrap\">
                                                    <thead>
                                                    <tr>
                                                        <th>Nom</th>
                                                        <th>Année</th>
                                                        <th>Semestre</th>
                                                        <th>Niveau</th>
                                                        <th>Matieres</th>
                                                        <th>action</th>
                                                    </tr>
                                                    </thead>
                                                    <tbody>
                                                    {% for u in unite %}
                                                        {% if u.enabled == 1 %}
                                                            <tr>
                                                                <td>{{ u.nom }}</td>
                                                                <td>{{ u.annee.annee }}</td>
                                                                <td>{{ u.semestre.nom }}</td>
                                                                <td>{{ u.niveau.niveau }}</td>
                                                                <td>{% for m in u.matiere %}<span
                                                                    class=\"btn btn-outline-warning btn-xs show-Matiere\"
                                                                    data-target=\"#view-matiere\" data-toggle=\"modal\"
                                                                    data-id=\"{{ m.id }}\">{{ m.nom }}</span>{% endfor %}
                                                                    <a href=\"{{ path('admin_Matiere_add',{'id':u.id}) }}\" ><span class=\"icon-plus-overlay\">+</span></a></td>
                                                                <td>
                                                                    <a>
                                                                        <button type=\"button\"
                                                                                class=\"btn btn-outline-primary btn-xs update-Classe\"
                                                                                data-target=\"#view-update-classe\"
                                                                                data-toggle=\"modal\"
                                                                                data-id=\"{{ u.id }}\">Update
                                                                        </button>
                                                                    </a>
                                                                    <a>
                                                                        <button type=\"button\"
                                                                                class=\"btn btn-xs btn-outline-success btn-xs desactivt\"
                                                                                data-id=\"{{ u.id }}\"
                                                                                data-target=\"#view-contact\"
                                                                                data-toggle=\"modal\">Active
                                                                        </button>
                                                                    </a>
                                                                </td>
                                                            </tr>
                                                        {% else %}
                                                            <tr>
                                                                <td>{{ u.nom }}</td>
                                                                <td>{{ u.annee.annee }}</td>
                                                                <td>{{ u.semestre }}</td>
                                                                <td>{{ u.specialite.nomSpecialite }}</td>
                                                                <td>{{ u.niveau.niveau }}</td>
                                                                <td>{% for m in u.matiere %}
                                                                    <button
                                                                    class=\"btn btn-outline-warning btn-xs show-Matiere\"
                                                                    data-target=\"#view-matiere\" data-toggle=\"modal\"
                                                                    data-id=\"{{ m.id }}\"
                                                                    disabled>{{ m.nom }}</button>{% endfor %}<span
                                                                            class=\"icon-plus-overlay add-Matiere\"
                                                                            data-target=\"#view-add-matiere\"
                                                                            data-toggle=\"modal\"
                                                                            data-id=\"{{ u.id }}\">+</span></td>
                                                                <td>
                                                                    <a>
                                                                        <button type=\"button\"
                                                                                class=\"btn btn-outline-primary btn-xs\"
                                                                                disabled>Update
                                                                        </button>
                                                                    </a>
                                                                    <a>
                                                                        <button type=\"button\"
                                                                                class=\"btn btn-xs btn-outline-danger desactivt\"
                                                                                data-id=\"{{ u.id }}\"
                                                                                data-target=\"#view-contact\"
                                                                                data-toggle=\"modal\">Desactive
                                                                        </button>
                                                                    </a>
                                                                </td>
                                                            </tr>
                                                        {% endif %}
                                                    {% endfor %}
                                                    </tbody>
                                                </table>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </section>



    <div id=\"view-contact\" class=\"modal fade\">
        <div class=\"modal-dialog\" role=\"document\">
            <div class=\"modal-content\">
                <div class=\"modal-header\">
                    <button type=\"button\" class=\"close contact-close\" data-dismiss=\"modal\" aria-label=\"Close\">
                        <span aria-hidden=\"true\">&times;</span>
                    </button>
                    <h4 class=\"modal-title\">Activation Unite</h4>
